export const addGameToCart = (prodotto, prodottoID) => {
    if (!prodotto || !prodottoID) return;

    let ls = JSON.parse(localStorage.getItem("cart"));

    if (!ls) {
      ls = {};
    }

    if (!ls[prodottoID]) {
      const cartItem = {
        title: prodotto.title,
        id: prodottoID,
        quantity: 1,
      };

      ls[prodottoID] = cartItem;
    } else {
      ls[prodottoID].quantity++;
    }

    localStorage.setItem("cart", JSON.stringify(ls));
  };
