import { addGameToCart } from '../core/addGameToCart';
//import { ProdottoList } from '../prodottolist/ProdottoList';
import arrow from './icone/arrow.jpg';
import { useId } from 'react';

export const ProductDescription = ({

    title = "",
    about = "",
    size = "",
    price = "",
}) => {

    const vID = useId()
    return (
        <div>
             {/* const vID = useId(); <h1>ID {vID}</h1> */}
            <h2>{`${title}`} </h2>
            <p>{`${about}`}</p>
            <h3>{`${size}`}</h3>
            <h4>{`${price}`}</h4>

            <button onClick={() => { 
                addGameToCart({
                title,
                about,
                size,
                price,
                },
                vID

                );
            }}> 

            Compra Ora   <img src={arrow} alt="freccia" /> 

            </button>
        </div>
    );
};
