import React, { useState } from 'react'
import arrow from './arrow.jpg';
import iconaMenu from './menu.jpg';
import controller from './controller.png';
import shoppingchart from './shoppingchart.png';
import add from './add.png';
import facebook from './fb.png';
import twitter from './tw.png';
import instagram from './ig.png';
import './prodotto.css';
import { Modal } from '../modal/Modal';

export function Prodotto() {
    const [modalOpen, setModalOpen] = useState(false);
    return (
        <div className='container'>
            <div className='navbar'>
                <img src={iconaMenu} className='logo' alt="iconacarrello" />
                <nav>
                    <ul>
                        <li><a href=''>Chi siamo</a></li>
                        <li><a href=''>Scopri di più</a></li>
                        <li><a href=''>Blog</a></li>
                        <li><a href=''>Contatti</a></li>

                    </ul>

                </nav>
                <img src={shoppingchart} className="shopping-icon" onClick={() => {
                    setModalOpen(true);
                }} alt="carrello" />


            </div>
            <div className='row'>
                <div className='col-1'>
                    <h2>PS4 v2 <br /> Dual</h2>
                    <h3>wireless</h3>
                    <p>(compatible)</p>
                    <h4>32.50$</h4>
                    <button type='button'>Compra Ora <img src={arrow} alt="freccia" /> </button>

                </div>

                <div className='col-2'>
                    <img src={controller} className="controller" alt="fotoprodotto" />
                    <div className='color-box'> </div>

                    <div className='add-btn'>
                        <img src={add} alt="iconapiù" />
                        <p><small> Aggiungi al Carrello</small></p>

                    </div>

                </div>
            </div>
            <div className='social-links'>
                <a href='https://it-it.facebook.com/' target="_blank" rel="noopener noreferrer"><img src={facebook} alt="facebook" /></a>
                <a href='https://twitter.com/?lang=it' target="_blank" rel="noopener noreferrer"><img src={twitter} alt="twitter" /></a>
                <a href='https://www.instagram.com/' target="_blank" rel="noopener noreferrer"><img src={instagram} alt="instagram" /> </a>

            </div>
        </div>

    )
}
