import React, { useState } from "react";
import './newsletter.css';


export function Newsletter() {

    const [email, setEmail] = useState('')
    const [name, setName] = useState('')

/*
    const insertUser = (e) => {

        e.preventDefault();

        const requestOptions = {

            method: 'POST',
