import controller from './controller.png';
import cestino from './cestino.png';
import axios from 'axios';

let ls = JSON.parse(localStorage.getItem("cart"));

export const CartItem = (props) => {
/*
  const RemoveItemFromCart = (props) => {
    console.log("cancellato")

    const del = async () => {
      const response = await axios.delete("http://localhost:8080//delProduct/" + 4)
      return await response.data.json;
    };

    useEffect(() => del)
    return (
      <p>Contatto Eliminato!</p>
    );


  };
*/

  return (
    <div class="basket-product">
      <div class="item">
        <div class="product-image">
          <img src={controller} alt="Placholder Image 2" class="product-frame" />
        </div>
        <div class="product-details">
          <h1>{props.title} </h1>
          <p><strong>Navy, Size 10</strong></p>
          <p>{"Product Code" + props.id}</p>
        </div>
      </div>
      <div class="price">{props.price}</div>
      <div class="quantity">

        <input type="number" class="quantity-field"

        />
      </div>

      <div class="subtotal">{props.price}</div>
      <div class="remove">
        <button onClick={RemoveItemFromCart} ><img className="imgCestino" src={cestino} /> </button>
      </div>
    </div>
  );
};
