import React, { useEffect, useState } from 'react';
/*-----------ICONE--------------*/
//import arrow from './icone/arrow.jpg';
import iconaMenu from './icone/menu.jpg';
import controller from './icone/controller.png';
import shoppingchart from './icone/shoppingchart.png';
import add from './icone/add.png';
import facebook from './icone/fb.png';
import twitter from './icone/tw.png';
import instagram from './icone/ig.png';
/*----------ICONE---------------*/

import './prodotto.css';

import axios from "axios";

import { ProdottoList } from '../prodottolist/ProdottoList';


export const Prodotto = ({ title = "", about = "", size = "", price = "" }) => {

    const [prodottiData, setProdottiData] = useState([]);

    useEffect(() => {
        axios.get("./prodotti.json").then((res) => {
            setProdottiData(res.data);
        });
    }, []);


    return (
        <div className='prodottoContainer'>
            <div className='prodottoNavbar'>
                <img src={iconaMenu} className='prodottoLogo' alt="iconacarrello" />
                <nav>
                    <ul>
                        <li><a href=''>Chi siamo</a></li>
                        <li><a href=''>Scopri di più</a></li>
                        <li><a href=''>Blog</a></li>
                        <li><a href=''>Contatti</a></li>

                    </ul>
                </nav>
                    <img src={shoppingchart} className="shopping-icon" 
                        onClick={() => {}} alt="carrello" />
            </div>


            <div className='row'>
                <div className='col-1'>
                    <ProdottoList prodotti={prodottiData} />
                </div>

                <div className='col-2'>
                    <img src={controller} className="controller" alt="fotoprodotto" />
                    <div className='color-box'> </div>

                    <div className='add-btn'>
                        <img src={add} alt="iconapiù" />
                        <p><small onClick={() => {
                            console.log('Aggiunto al carrello')
                        }}> Aggiungi al Carrello</small></p>

                    </div>

                </div>
            </div>
            <div className='social-links'>
                <a href='https://it-it.facebook.com/' target="_blank" rel="noopener noreferrer"><img src={facebook} alt="facebook" /></a>
                <a href='https://twitter.com/?lang=it' target="_blank" rel="noopener noreferrer"><img src={twitter} alt="twitter" /></a>
                <a href='https://www.instagram.com/' target="_blank" rel="noopener noreferrer"><img src={instagram} alt="instagram" /> </a>

            </div>
        </div>

    )
}
